import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { Container, Navbar, Button } from "shards-react";

export default class MainNavbar extends React.Component {
  constructor(props) {
    super(props);

    this.ConnectionWallet = this.ConnectionWallet.bind(this);
  }

   //connection wallet 비동기 지갑 처리 
  ConnectionWallet() {
    const accounts =  window.ethereum.request({
        method: "eth_requestAccounts",
    });
    console.log(accounts[0])
  }

  //connection wallet 비동기 지갑 처리 

  render() {
    return(
    <div>
      <Container className="p-0">
        <Navbar type="light" className="align-items-stretch flex-md-nowrap p-0">
        <div className="d-table m-auto">
          <img
            id="main-logo"
            className="d-inline-block align-top mr-1"
            style={{ maxWidth: "25px" }}
            src={require("./../../../images/content-management/minting-lofo.png")}
            alt="Shards Dashboard"
            />
          </div>
          <Button  theme="primary" className="mb-2 mr-1" style={{marginTop:5}}>Connect Wallet</Button>
        </Navbar>
      </Container>
    </div>
    );
  }
}
